gdml_script:
extends Area2D

const Side := {
    "NONE": "none",
    "LEFT": "left",
    "RIGHT": "right"
}

var side: String = Side.NONE

var up: int
var down: int

func _ready():
    var cs = CollisionShape2D.new()
    var rect = RectangleShape2D.new()
    rect.extents = Vector2(32, 32)
    cs.shape = rect
    add_child(cs)

    var sprite = Sprite.new()
    var image = Image.new()

    image.load("res://icon.png")
    var image_texture = ImageTexture.new()
    image_texture.create_from_image(image)
    sprite.texture = image_texture
    add_child(sprite)

    scale.y = 3
    match side:
        Side.LEFT:
            up = KEY_W
            down = KEY_S
        Side.RIGHT:
            up = KEY_UP
            down = KEY_DOWN
        _:
            push_error("No side set")

    connect("body_entered", self, "_on_body_entered")

func _physics_process(delta):
    if Input.is_key_pressed(up):
        position.y -= 10
    if Input.is_key_pressed(down):
        position.y += 10

func _on_body_entered(body):
    if body.name != "Ball":
        return
    body.linear_velocity = Vector2.ZERO
    body.apply_central_impulse(Vector2(rand_range(300, 150) * (-1 if side == Side.RIGHT else 1), rand_range(-300, 300)))

gdml.label:

extends Label

func _ready():
    var tree = get_tree()
    yield(tree, "idle_frame")

    var ball_vis = tree.root.find_node("BallVis", true, false)
    ball_vis.connect("screen_exited", self, "_on_vis_exit")

func _on_vis_exit() -> void:
    text = "ball is gone!"


